 function menudeAra(x) {    /*Codding by  Cihan MENGE anasayfa arama*/    var filter = x.value, count = 0;    $("ul.sidebar-menu li").each(function () {        if ($(this).text().search(new RegExp(filter, "i")) < 0) {            $(this).fadeOut("fast");        } else {            $(this).show("fast");            $(this).find(".treeview-menu").show("fast");            count++;        }    });    var numberItems = count;    if (x.value.length == 0) { $(".treeview-menu").each(function () { $(this).hide(); }); }}/*-------------- kaydetme scripti--------------------*/function Kaydet(yol,islem,veri) {    $("form#kaydet").attr("action","/"+yol+"/Kaydet.php?islem="+islem+"&id="+veri);}/*--------------düzenleme scripti--------------------*/function Duzenle(yol,islem,veri){    $.ajax({        url: '/'+yol+'/Duzenle.php?islem='+islem+'&id='+veri,        type: 'GET',        success: function (e) {            $('#duzenle_load').html(e);        }    });}function Duzenle_kat(yol,islem,veri){    $.ajax({        url: '/'+yol+'/Duzenle.php?islem='+islem+'&id='+veri,        type: 'GET',        success: function (e) {            $('#duzenle_load_kat').html(e);        }    });}/*--------------silme scripti--------------------*/function Sil(yol,islem,veri){    $('#modalSil').modal('show');    $.ajax({        url: '/'+yol+'/Load.php?islem='+islem+'&id='+veri,        type: 'GET',        success: function (e) {            $('#load_uyari').text(e);        }    });    $("form#veriyi_sil").attr("action","/"+yol+"/Sil.php?islem="+islem+"&id="+veri);}/*--------------çok satılanlardan silme scripti--------------------*/function Silme(yol,islem,veri){    $.ajax({        url: '/'+yol+'/Sil.php?islem='+islem+'&id='+veri,        type: 'GET',        success: function (e) {            $('#durum').html(' <div class="alert alert-danger" role="alert">Ürününüz çok satılanlar kategorisinden silindi ! <button type="button" class="close" data-dismiss="alert" aria-label="Close"> <span aria-hidden="true">&times;</span> </button></div>');        }    });    setTimeout(function(){        location.reload();    }, 3000);} /*--------------çok satılanlar scripti--------------------*/ function Ekle(yol,islem,veri){     $.ajax({         url: '/'+yol+'/Kaydet.php?islem='+islem+'&id='+veri,         type: 'GET',         success: function (e) {             $('#durum').html(' <div class="alert alert-success" role="alert">Ürününüz çok satılanlar kategorisine eklendi ! <button type="button" class="close" data-dismiss="alert" aria-label="Close"> <span aria-hidden="true">&times;</span> </button></div>');         }     });     setTimeout(function(){         location.reload();     }, 3000); }function veri_guncelle(yol,islem,veri) {    $("form#veriyi_guncelle").attr("action", "/"+yol+"/Guncelle.php?islem="+islem+"&id="+veri);}//yenileme scriptifunction Yenile() {    location.reload();}/*slider düzenleme fonksiyonu*/function slider_duzenle(yol,islem,id) {    $('#slider_duzenle').modal('show');    $.ajax({        url: '/'+yol+'/Duzenle.php?islem='+islem+'&id='+id,        type: 'GET',        success: function (e) {            $('#slider_duzenleme').html(e);        }    });    $("form#veriyi_guncelle").attr("action","/"+yol+"/Guncelle.php?islem="+islem+"&id="+veri);} $('#kategori_id').on('change', function() {     var id = $('#kategori_id').val();     $.ajax({         url: '/admin/Load.php?islem=alt_kategori_cek&id='+id,         type: 'GET',         success: function (e) {             $('#slider_load').empty();             $('#slider_load').html(e);         }     }); });