      <?php    /*      yazar: Ömer ABUL - Cihan MENGE */      class Sayfalama_galeri {      public $toplam;      public $sayfa_basina;      public $sayfa_sayisi;      public $url;      public $baslangic;      public $ilerigeri;      public $ilkson;      public $sayfa;      private $set;	        function __construct() 	  {		  $this->sayfa_basina=10;		  $this->baslangic='0,10';		  $this->ilerigeri=true;		  $this->ilkson=true;		  $this->sayfa=1;		  $this->set=false;      }      function hazirla()      {          $this->sayfa=$this->sayfa < 1 ? 1 :$this->sayfa;          $this->sayfa_sayisi=ceil($this->toplam/$this->sayfa_basina);          $this->baslangic=(($this->sayfa-1)*$this->sayfa_basina);          $this->set=true;      }	  	        function link($sayfa) 	  {		return sprintf($this->url,$sayfa);      }	        function sayfala() 	  {		  if($this->set==false) $this->hazirla();			  $ilk_sayfa=$this->sayfa-5;			  $son_sayfa=$this->sayfa+5;			  		  if($this->ilkson==true) 		  {			  $ilk_link='<li class="page-item"><a href="'.$this->link(1).'" class="page-link page-links"><i class="bx bx-chevrons-left"></i></a></li>';			  $son_link='<li class="page-item"><a href="'.$this->link($this->sayfa_sayisi).'" class="page-link page-links"><i class="bx bx-chevrons-right"></i></a></li>';		  } else $ilk_link=$son_link='';		  if($ilk_sayfa <=1 ){$ilk_sayfa=1;$ilk_link='';}		  if($son_sayfa >= $this->sayfa_sayisi ){$son_sayfa=$this->sayfa_sayisi;$son_link=' ';}		  $sayfalar='<!-- sayfalama başlangıç --><ul class="pagination">';		  $sayfalar.='<li class="page-item"><a href="'.$this->link(1).'" class="page-link page-links"><i class="bx bx-chevrons-left"></i></a></li>';		  for($i=$ilk_sayfa;$i<=$son_sayfa;$i++){		  $class= ($this->sayfa==$i) ? 'page-item active' : 'nav-item';		  $sayfalar.='<li class="'.$class.'"><a href="'.$this->link($i).'" class="page-link" title="'.$i.'. Sayfa"><span>'.$i.'</span></a></li>';		  }		  $sayfalar.='<li class="page-item"><a href="'.$this->link($this->sayfa_sayisi).'" class="page-link page-links"><i class="bx bx-chevrons-right"></i></a></li>';		  $sayfalar.='</ul><!-- sayfalama bitiş -->';		  return $sayfalar;      }      }      ?> 